ody.innerHTML = printcontent;        	window.print();        	document.body.innerHTML = restorepage;        window.location.reload();      // return true;}$('#city_id').click(function(){		$city_id=$('#city_id').val();		//alert($city_id);				/*$.post('<?php echo base_url();?>index.php?admin/get_city_doctors/', { city_id: $city_id }, function(data){				if(data)				{   				d = json_decode(data);				alert(d);									}			  			});		*/			});</script>