              $('.result').empty();                    $('.result').attr("data-toggle", "dropdown");                    $('.result').dropdown('toggle');                }                else if (data.length == 0) {                    $('.result').attr("data-toggle", "");                }                $.each(data, function (key,value) {                    if (data.length >= 0)                       var name= value['name'];                       var id= value['medicine_id'];                        $('.result').append('<li role="presentation" onclick="addmed(\'' +name+'\')" style="cursor:pointer" data-id="name">' + name + '</li>');                });             }                    });    } function addmed(str){     //alert(str);    $('.search:last').val(str);   }      </script>    